mapper.delete:
SET @target_pos = (SELECT pos FROM category_tbl WHERE id = #{id});
UPDATE category_tbl SET pos = pos - 1 WHERE pos > @target_pos;
DELETE FROM category_tbl WHERE id = #{id};

mapper.update:
UPDATE category_tbl SET pos = #{pos}+100 WHERE pos = #{pos}-1;
UPDATE category_tbl SET pos = #{pos}-1 WHERE pos = #{pos};
UPDATE category_tbl SET pos = #{pos} WHERE pos = #{pos}+100;

UPDATE category_tbl SET pos = #{pos}+100 WHERE pos = #{pos}+1;
UPDATE category_tbl SET pos = #{pos}+1 WHERE pos = #{pos};
UPDATE category_tbl SET pos = #{pos} WHERE pos = #{pos}+100;
