mapper.select:
select pt.id, pt.category_tbl_id, pt.user_tbl_id, pt.subject, pt.content, pt.type, pt.created_at, ht.content as hashcontent from post_tbl pt left join post_hashtag_tbl pht on pt.id = pht.post_tbl_id left join hashtag_tbl ht on pht.hashtag_tbl_id = ht.id where
ht.content = #{hashtag}
and pt.deleted = 0 group by pt.id

select id
from hashtag_tbl
where content = #{content}

select count(*) > 0
from hashtag_tbl
where content = #{content}

select count(*) > 0
from post_hashtag_tbl
where post_tbl_id = #{postId} and hashtag_tbl_id = #{hashtagId}


select count(*) > 0
from post_like_tbl
where post_tbl_id = #{postId} and user_tbl_id = #{userId}
